const spl = require("../../modules/spl/spl.js");

// Set SPlectrum and client root directory, extract command string
var root = process.cwd().split("/");
if (root.length === 1 ) root = root[0].split(`\\`);
const splRoot = root.slice(0,root.length-2).join("/");
const session = root[root.length-1];
const clientRoot = root.slice(root.length - 2).join("/")
const commandString = process.argv.slice(2);

var command = {
    headers: { 
        spl: { 
            execute: { cwd: splRoot, session: session, modules: "./modules", TTL: 100 }, 
            request: { 
                action: "spl/command/execute", 
                "spl/command/execute": { 
                    set: {
                        commandString: commandString,
                        template: { repo: clientRoot, dir: "commands", file: "command.json", copy: [ "spl/command" ] },
                        UUID: spl.generateUUID()
                    }
                }
            }
        } 
    },
    value: {}
}
console.dir(command,{depth:10});
process.nextTick ( () => spl.moduleAction( command, "spl/execute/execute" ) );

setTimeout ( function printComplete () {
    if( command.headers.spl.execute.finishTime ) console.dir(command,{depth:10});
    else { console.log(command.headers.spl.execute.action); setTimeout ( printComplete, 10); }
}, 10);
